import React, { useState, useEffect, useRef } from 'react';

// Custom Hook for detecting when an element is on screen
const useOnScreen = (options) => {
    const ref = useRef();
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        const observer = new IntersectionObserver(([entry]) => {
            if (entry.isIntersecting) {
                setIsVisible(true);
                observer.unobserve(entry.target);
            }
        }, options);

        if (ref.current) {
            observer.observe(ref.current);
        }

        return () => {
            if (ref.current) {
                observer.unobserve(ref.current);
            }
        };
    }, [ref, options]);

    return [ref, isVisible];
};

// Main App component which will contain all other components
export default function App() {
  const socialLinks = {
    linkedin: "https://linkedin.com/in/narayanasamy",
    github: "https://github.com/narayanasamy",
    email: "anarayanasamy12@gmail.com"
  };

  const experienceData = [
    {
      company: "FREELANCER",
      role: "iOS Developer",
      period: "July 2023 - Present",
      duties: [
        "Developed the mEinsteinAI chat application with real-time messaging and AI-powered features.",
        "Implemented voice interaction and offline storage using CoreData with performance optimization.",
        "Applied MVVM architecture and integrated push notifications using Agile development."
      ]
    },
    {
      company: "Fusion Innovative Limited",
      role: "iOS Developer",
      period: "February 2023 - June 2023",
      duties: [
        "Built an order tracking module for the Fusion Kitchen food delivery app.",
        "Implemented real-time tracking using SwiftUI and Firebase with performance optimization.",
        "Collaborated on multiple client apps including Chennai Dosa and Kolkata Restaurant."
      ]
    },
    {
      company: "Webandcrafts",
      role: "Jr. iOS Developer",
      period: "March 2022 - February 2023",
      duties: [
        "Enhanced the IKEA mobile app, developing login and OTP screens for 400,000+ users.",
        "Built the home page for the Lulu Shopping app using SwiftUI framework with performance optimization."
      ]
    }
  ];

  const skillsData = {
    languages: ["Swift", "SwiftUI"],
    frameworks: ["UIKit", "CoreData", "Realm", "Combine", "Alamofire", "Firebase", "Xcode", "Git"],
    architecture: ["MVVM", "The Composable Architecture (TCA)"]
  };

  // AnimatedSection component for a fade-in effect on scroll.
  const AnimatedSection = ({ children, id, className }) => {
    const [ref, isVisible] = useOnScreen({ threshold: 0.1 });
    return (
      <section 
        ref={ref} 
        id={id} 
        className={`transition-opacity duration-1000 ease-in-out ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-5'} ${className}`}
      >
        {children}
      </section>
    );
