// Main App Component
export default function App() {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [jobRole, setJobRole] = useState('');
    const [emailOutput, setEmailOutput] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleGenerateEmail = async () => {
        if (!jobRole) {
            setError("Please enter a job role to generate an email draft.");
            return;
        }

        setLoading(true);
        setError('');
        setEmailOutput('');

        const prompt = `Based on the following iOS Developer portfolio data, write a short, professional email draft for a cover letter. The email should be a single, pre-formatted block of text with proper line breaks, suitable for a job application. The tone should be confident and highlight how my skills and experience match the role of a "${jobRole}".

Portfolio Data:
- Professional Summary: "${portfolioData.professionalSummary}"
- Skills: ${Object.entries(portfolioData.skills).map(([key, value]) => `${key}: ${value.join(', ')}`).join('\n')}
- Experience: ${portfolioData.experience.map(exp => `Company: ${exp.company}, Role: ${exp.role}, Duties: ${exp.duties.join('; ')}`).join('\n')}

Email structure should be:
Subject: Application for ${jobRole} position
Body:
- Salutation (e.g., Dear Hiring Manager,)
- Brief introduction stating the position I'm applying for and where I saw it.
- A paragraph highlighting my core skills and experience, directly relating them to the "${jobRole}" role. Mention specific keywords from the data provided, such as "real-time messaging," "AI integration," "SwiftUI," "MVVM," and "performance optimization."
- A sentence or two expressing enthusiasm for the opportunity and my belief that my background would be a valuable asset.
- Closing (e.g., Thank you for your time and consideration.)
- Signature (e.g., Sincerely, Narayanasamy A)`;

        let retryCount = 0;
        const maxRetries = 3;
        while (retryCount < maxRetries) {
            try {
                const chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
                const payload = { contents: chatHistory };
                const apiKey = "";
                const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

                const response = await fetch(apiUrl, {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(payload)
                });
                const result = await response.json();

                if (result.candidates && result.candidates.length > 0 &&
                    result.candidates[0].content && result.candidates[0].content.parts &&
                    result.candidates[0].content.parts.length > 0) {
                    const text = result.candidates[0].content.parts[0].text;
                    setEmailOutput(text);
                    setLoading(false);
                    return;
                } else {
                    throw new Error('Invalid API response structure.');
                }
            } catch (err) {
                console.error('API call failed:', err);
                retryCount++;
                if (retryCount >= maxRetries) {
                    setError('Failed to generate the email draft. Please try again later.');
                    setLoading(false);
                    return;
                }
                await new Promise(res => setTimeout(res, 2 ** retryCount * 1000));
            }
        }
    };

    const handleGoToTop = () => {
        window.scrollTo({ top: 0, behavior: 'smooth' });
    };

    const [showGoTop, setShowGoTop] = useState(false);
    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 200) {
                setShowGoTop(true);
            } else {
                setShowGoTop(false);
            }
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <div className="bg-gray-950 text-gray-200 min-h-screen font-sans">
            <style>
                {`
                @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700&display=swap');
                body {
                    font-family: 'Inter', sans-serif;
                    background-color: #0d1117;
                    color: #E5E5E5;
                }
                `}
            </style>
            <Header />
            <main>
                <Hero />
                <About onGenerateClick={() => setIsModalOpen(true)} />
                <Experience />
                <Skills />
                <Projects />
                <Education />
                <Contact />
            </main>
            <Footer />
            <Modal
                show={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onGenerate={handleGenerateEmail}
                loading={loading}
                emailOutput={emailOutput}
                jobRole={jobRole}
                setJobRole={setJobRole}
                error={error}
            />
            <button
                onClick={handleGoToTop}
                className={`fixed bottom-8 right-8 bg-red-600 text-white p-4 rounded-full shadow-lg transition-transform duration-300 hover:scale-110 ${showGoTop ? 'opacity-100 visible' : 'opacity-0 invisible'}`}
            >
                <i className="fas fa-arrow-up"></i>
            </button>
        </div>
    );
}
